eng_aze = {}
for i in range(0, len(word_list)-1, 2):
    eng_aze[word_list[i]] = word_list[i+1]

while True:
    search = input('Please enter word: ')
    if search in eng_aze:
        print(f'{search} in azeri language is {eng_aze[search]}')
    else:
        question = input('This word isn\'t in vocabulary. Do you want to add? ')
        if question == 'yes':
            az_word = input('Please enter translation of this word: ')
            eng_aze[search] = az_word.lower()
            print(f'{search} in azeri language is {eng_aze[search]}')
        else:
            q2 = input('Do you want to see entire vocabulary? ')
            if q2 == 'yes':
                for eng, aze in eng_aze.items():
                    print(f'{eng} : {aze}')
            else:
                print('Exiting program')
                break
